INSERT INTO "SUBCULTURE" ("subculture_id", "slug", "subculture_name", "traditional_greeting", "greeting_meaning", "explanation", "culture_id", "status", "display_priority_status", "conservation_status", "created_at", "updated_at") VALUES (5, 'samin', 'Samin', 'Sami-sami', '(you''re welcome, reflecting equality teachings)', 'The Samin subculture from the Blora border (East-Central Java region), followers of Samin Surosentiko''s teachings; distinctive features include values of simplicity, non-violence, and colonial resistance; a closed community focused on agriculture and harmonious life with nature.1', 6, 'PUBLISHED', 'LOW', 'MAINTAINED', '2025-11-25T19:45:51.559Z', '2025-11-25T19:45:51.559Z');
INSERT INTO "SUBCULTURE" ("subculture_id", "slug", "subculture_name", "traditional_greeting", "greeting_meaning", "explanation", "culture_id", "status", "display_priority_status", "conservation_status", "created_at", "updated_at") VALUES (8, 'madura-bawean', 'Madura-Bawean', 'Abeje ', '(hello, Madurese variation)', 'The Madura-Bawean subculture from Bawean Island, a mix of Madurese with Malay-Javanese; distinctive features include migration traditions (boyan), mat weaving, and honesty values; the community is mostly migrants abroad.', 6, 'PUBLISHED', 'LOW', 'TREATED', '2025-11-25T19:45:51.559Z', '2025-11-25T19:45:51.559Z');
INSERT INTO "SUBCULTURE" ("subculture_id", "slug", "subculture_name", "traditional_greeting", "greeting_meaning", "explanation", "culture_id", "status", "display_priority_status", "conservation_status", "created_at", "updated_at") VALUES (6, 'mataraman', 'Mataraman', 'Sugeng dalu ', '(good evening, in Mataraman dialect)', 'The Matraman subculture from western East Java areas like Ngawi, Madiun; influenced by Yogyakarta-Solo palace culture, distinctive features include refined Javanese language, ketoprak arts, and high values of hierarchy and politeness.', 6, 'PUBLISHED', 'LOW', 'MAINTAINED', '2025-11-25T19:45:51.560Z', '2025-11-25T19:45:51.560Z');
INSERT INTO "SUBCULTURE" ("subculture_id", "slug", "subculture_name", "traditional_greeting", "greeting_meaning", "explanation", "culture_id", "status", "display_priority_status", "conservation_status", "created_at", "updated_at") VALUES (1, 'arekan', 'Arekan', 'Rek', ' (casual greeting like "bro" in Surabaya dialect)', 'The Arekan subculture originates from Surabaya and surrounding areas, known for its rough Javanese dialect (Suroboyoan), urban culture like ludruk, and values of bravery and humor; the community is predominantly East Javanese with modern influences.', 6, 'PUBLISHED', 'LOW', 'MAINTAINED', '2025-11-25T19:45:51.560Z', '2025-11-25T19:45:51.560Z');
INSERT INTO "SUBCULTURE" ("subculture_id", "slug", "subculture_name", "traditional_greeting", "greeting_meaning", "explanation", "culture_id", "status", "display_priority_status", "conservation_status", "created_at", "updated_at") VALUES (2, 'panaragan', 'Panaragan', 'Iki Lho Dlondonge Wong Panaraga', 'We are Children of Panaraga People.', 'Introduction
INSERT INTO "SUBCULTURE" ("subculture_id", "slug", "subculture_name", "traditional_greeting", "greeting_meaning", "explanation", "culture_id", "status", "display_priority_status", "conservation_status", "created_at", "updated_at") VALUES (7, 'madura', 'Madura', 'Abeje ', '(hello, in Madurese language)', 'The Madura subculture from Madura Island, Madurese ethnicity with distinctive features like karapan sapi (bull racing), Madurese language, and high self-esteem values; the community is religious (Islamic), focused on fishing, salt farming, and migration traditions.', 6, 'PUBLISHED', 'LOW', 'MAINTAINED', '2025-11-06T05:42:49.407Z', '2025-11-06T05:42:49.934Z');
INSERT INTO "SUBCULTURE" ("subculture_id", "slug", "subculture_name", "traditional_greeting", "greeting_meaning", "explanation", "culture_id", "status", "display_priority_status", "conservation_status", "created_at", "updated_at") VALUES (3, 'pandhalungan', 'Pandhalungan', 'Sugeng enjing ', '(good morning, a mix of Javanese-Madurese)', 'The Pandalungan subculture from the Tapal Kuda region (such as Jember, Lumajang), a result of Javanese and Madurese ethnic mixing; distinctive hybrid features like rujak cingur food, mixed language, and egalitarian values in farming communities.', 6, 'PUBLISHED', 'LOW', 'TREATED', '2025-11-06T05:42:45.207Z', '2025-11-06T05:42:45.732Z');
INSERT INTO "SUBCULTURE" ("subculture_id", "slug", "subculture_name", "traditional_greeting", "greeting_meaning", "explanation", "culture_id", "status", "display_priority_status", "conservation_status", "created_at", "updated_at") VALUES (9, 'madura-kangean', 'Madura-Kangean', 'Abeje ', '(hello, Madurese variation)', 'The Madura-Kangean subculture from the Kangean Islands, eastern Madurese ethnicity with Bugis-Makassar mix; distinctive features include buffalo fighting, Kangean language, and resilience values; focused on fishing and agriculture in remote islands.1', 6, 'PUBLISHED', 'LOW', 'TREATED', '2025-11-06T05:42:51.508Z', '2025-11-06T05:42:52.033Z');
INSERT INTO "SUBCULTURE" ("subculture_id", "slug", "subculture_name", "traditional_greeting", "greeting_meaning", "explanation", "culture_id", "status", "display_priority_status", "conservation_status", "created_at", "updated_at") VALUES (4, 'osing', 'Osing', 'Sugeng rawuh ', '(welcome, in Osing language)', 'The Osing subculture from Banyuwangi, descendants of the Blambangan Kingdom; distinctive features include Osing language (similar to ancient Javanese), gandrung dance, ider bumi tradition, and Hindu-Buddhist beliefs mixed with Islam; focused on preserving Majapahit heritage.', 6, 'PUBLISHED', 'LOW', 'MAINTAINED', '2025-11-06T05:42:46.257Z', '2025-11-06T05:42:46.781Z');
