INSERT INTO "LEXICON" ("lexicon_id", "slug", "lexicon_word", "ipa_international_phonetic_alphabet", "transliteration", "etymological_meaning", "cultural_meaning", "common_meaning", "translation", "variant", "variant_translations", "domain_id", "conservation_status", "contributor_id", "status", "created_at", "updated_at") VALUES (61, 'ponorogo', 'Ponorogo', 'pOnOrOgO', 'pOnOrOgO', 'NaN', 'Kata pana berarti melihat secara teliti, cermat, dan raga berarti badan, dan atau diri. Berdasarkan sudut pandang ini, Ponorogo berarti melihat diri sendiri dengan cermat, atau lebih popular dengan istilah pandai mawas diri. Pandangan lain menafsirkan Panaraga berkaitan dengan istilah “panraga skar” menjadi Panaraga yang terdapat dalam prasasti Watukura tahun 902 Masehi yang berarti “persembahan bunga” (Poesponegoro & Notosusanto, 1984). Dari makna tersebut, dapat dikiaskan sebutan Ponorogo sebagai suatu ungkapan batin (sikap legawa) sebagai petanda syukur kepada Sang Pencipta atas perkenaannya berbuat sesuatu yang dapat dinikmati orang lain baik dalam bentuk lisan maupun tulisan.', 'NaN', 'NaN', NULL, NULL, undefined, 'undefined', 1, 'DRAFT', '2025-11-12T07:39:55.452Z', '2025-11-12T07:39:55.452Z');
