INSERT INTO "LEXICON" ("lexicon_id", "slug", "lexicon_word", "ipa_international_phonetic_alphabet", "transliteration", "etymological_meaning", "cultural_meaning", "common_meaning", "translation", "variant", "variant_translations", "domain_id", "conservation_status", "contributor_id", "status", "created_at", "updated_at") VALUES (74, 'warok-sepuh', 'Warok sepuh', 'warOk sêpuh', 'warOk sêpuh', 'NaN', 'Warok sepuh menggambarkan sosok berwibawa dengan membawa teken ‘tongkat’ sambil mengamati dan mengarahkan warok muda (Gambar 2.4) yang sedang berlatih ilmu kanuragan. Wujud ekspresi “pendadaran” warok sepuh kepada warok muda yang tecermin dari polah bawa ‘tingkah laku’ terungkap dari gerakan warok muda seperti sembahan, yakni bentuk penghormatan murid kepada guru. Hal ini merupakan bentuk adab mulia siswa warok dalam mencari rida seorang guru. Dari substansi makna gerakan tari ini, dapat diambil simpulan seorang warok sepuh mengajarkan ilmu keutamaan hidup dan kehidupan yang hakiki sebagai bentuk kaderisasi atau pewarisan nilai-nilai adiluhung; dan adab menuntut ilmu seorang warok muda dengan menghormati guru (warok sepuh) merupakan kunci keberhasilan seorang murid dalam menuntut ilmu.', 'Warok Sepuh adalah sosok warok senior berwibawa yang membawa tongkat (teken) dan berperan sebagai guru pembimbing dalam mengajarkan ilmu kanuragan serta nilai-nilai keutamaan hidup kepada warok muda sebagai bentuk kaderisasi nilai adiluhung.', 'NaN', NULL, NULL, undefined, 'undefined', 1, 'DRAFT', '2025-11-12T07:40:04.678Z', '2025-11-12T07:40:04.678Z');
