INSERT INTO "LEXICON" ("lexicon_id", "slug", "lexicon_word", "ipa_international_phonetic_alphabet", "transliteration", "etymological_meaning", "cultural_meaning", "common_meaning", "translation", "variant", "variant_translations", "domain_id", "conservation_status", "contributor_id", "status", "created_at", "updated_at") VALUES (71, 'reog-wengker', 'Reog Wengker', 'reog wêngkêr', 'reog wêngkêr', 'NaN', 'lahir dari sejarah pan jang politik radikal dengan suatu tradisi pemberontakan yang telah masyhur di kalangan masyarakat Ponorogo. Dua sosok sentral dalam citra ini adalah warok dan gemblak', 'Reog Wengker adalah seni Reog yang berakar dari sejarah Kerajaan Wengker di Ponorogo dengan tradisi pemberontakan yang masyhur, berpusat pada sosok warok dan gemblak. Versi kontemporer Reog Wengker dikenal sebagai "Reog Obyog" yang tampil untuk acara pribadi dengan ciri khas tersendiri, berbeda dari Reog Ponorogo yang terstandarisasi di festival.', 'Reog Wengker is a form of Reog rooted in the history of the Wengker Kingdom in Ponorogo, known for its strong traditions associated with the figures of the warok and gemblak. The contemporary expression of Reog Wengker is known as Reog Obyog, which is performed at private or community events and features its own distinctive characteristics, distinguishing it from the standardized Reog Ponorogo typically presented in festival settings.', NULL, NULL, undefined, 'undefined', 1, 'PUBLISHED', '2025-11-12T07:40:02.548Z', '2025-11-12T07:40:02.548Z');
INSERT INTO "LEXICON" ("lexicon_id", "slug", "lexicon_word", "ipa_international_phonetic_alphabet", "transliteration", "etymological_meaning", "cultural_meaning", "common_meaning", "translation", "variant", "variant_translations", "domain_id", "conservation_status", "contributor_id", "status", "created_at", "updated_at") VALUES (72, 'reog-bantarangin', 'Reog Bantarangin', 'reog bantarangin', 'reog bantarangin', 'NaN', 'versi Reog pertama yang berasal dari Kerjaan Bantarangin (versi kulon kali). versi ini memuat cerita dengan aktor yang kompleks dalam dramatari, seperti  Warok, Satria Kuda Jathil, patih Bujangganing, Prabu Klomosewandana, hingga Dadak MErak. alur cerita menggambarkan semua peran aktor yang ditampilkan (Harsono, 2022). ', 'Reog Bantarangin adalah versi Reog pertama yang berasal dari Kerajaan Bantarangin (versi kulon kali) dengan struktur dramatari kompleks yang memuat tokoh-tokoh seperti Warok, Satria Kuda Jathil, Patih Bujangganing, Prabu Klomosewandana, hingga Dadak Merak dalam alur cerita yang menampilkan semua peran aktor secara pakem dan ketat.', 'Reog Bantarangin is considered the earliest form of Reog, originating from the Bantarangin Kingdom (the western river bank version). It features a complex dance-drama structure with characters such as the Warok that is the horse-riding Jathil warriors, Patih Bujangganing, Prabu Klomosewandana, and Dadak Merak. This version presents the storyline with clearly defined roles for each character and follows a strict, standardized performance structure.', NULL, NULL, undefined, 'undefined', 1, 'PUBLISHED', '2025-11-12T07:40:03.258Z', '2025-11-12T07:40:03.258Z');
INSERT INTO "LEXICON" ("lexicon_id", "slug", "lexicon_word", "ipa_international_phonetic_alphabet", "transliteration", "etymological_meaning", "cultural_meaning", "common_meaning", "translation", "variant", "variant_translations", "domain_id", "conservation_status", "contributor_id", "status", "created_at", "updated_at") VALUES (83, 'grebeg-sura', 'Grebeg sura', 'grêbêg sura', 'grêbêg sura', 'NaN', 'merupakan sebuah ikon dan identitas budaya etnik Ponoragan yang menyimpan banyak makna bagi masyarakat Ponoragan, baik dari aspek religius-spiritualis, kultural, historis, ekologis, ekonomis, maupun segi politis. Perhelatan ini menampilkan berbagai kegiatan yang ada dalam masyarakat Ponorogo, seperti festival Reog nasional (FRN), larung sesaji di Telaga Ngebel, lampah ratri sewudian, ziarah makam, jamasan pusaka, bedhal pusaka, kirab pusaka, grebeg tutup sura, dan lain sebagainya yang sangat kental dengan ritual dan kearifan lokal. Ritual dalam tradisi grebeg sura sangat kental dengan aura mistis dan magis begitu sangat terasa pada bulan ini. Semua yang berbau khas subetnik Jawa Ponoragan sangat tampak, utamanya didukung kebijakan pemerintah mewajibkan ber busana khas adat Ponoragan yang serba hitam selama acara grebeg sura.', 'NaN', 'NaN', NULL, NULL, undefined, 'undefined', 1, 'DRAFT', '2025-11-12T07:40:11.063Z', '2025-11-12T07:40:11.063Z');
INSERT INTO "LEXICON" ("lexicon_id", "slug", "lexicon_word", "ipa_international_phonetic_alphabet", "transliteration", "etymological_meaning", "cultural_meaning", "common_meaning", "translation", "variant", "variant_translations", "domain_id", "conservation_status", "contributor_id", "status", "created_at", "updated_at") VALUES (50, 'raden-batara-katong', 'Raden Batara Katong', 'raden batara katOng ', 'raden batara katOng ', 'NaN', 'Salah satu tokoh dalam sejarah berdirinya Ponorogo yang diceritakan dalam Babad Ponorogo (Sugianto, 2024: 1)', 'NaN', 'NaN', NULL, NULL, undefined, 'undefined', 1, 'DRAFT', '2025-11-12T07:39:47.607Z', '2025-11-12T07:39:47.607Z');
