INSERT INTO "LEXICON" ("lexicon_id", "slug", "lexicon_word", "ipa_international_phonetic_alphabet", "transliteration", "etymological_meaning", "cultural_meaning", "common_meaning", "translation", "variant", "variant_translations", "domain_id", "conservation_status", "contributor_id", "status", "created_at", "updated_at") VALUES (93, 'wiraswara', 'Wiraswara', 'wiraswara', 'wiraswara', 'NaN', 'Wiraswara merupakan tim senggakan yang bertugas sebagai penye mangat perjuangan dalam medan perang. Tim ini merupakan salah satu faktor penting dalam kesenian Reog. Perannya sebagai seni tarik suara yang terdiri dari kumpulan beberapa orang yang bertugas memotivasi pemain Reog dengan mengalunkan nada yang khas, yaitu ha’e, ha’e ho’ya ho’ya dengan suara yang gerok ‘serak’, keras, dan kompak yang dilakukan secara bersama-sama. Senggakan dalam kesenian Reog ibarat sebuah makanan tanpa kerupuk maka tidak akan terasa kriuk tanpa senggakan. Dalam Reog festival, senggakan biasanya dinamakan sebagai wiroswara yang bertugas menceritakan lelakon atau menggambarkan alur cerita seperti dalam pewayangan atau dalam dunia orkestra luar negeri, seperti soundtrack sehingga menjadikan penampilan kesenian Reog menjadi lebih hidup dan menarik. Wiraswara ini mengandung kearifan lokal gotong-royong, kerja sama, dan kekompakan dalam hidup dan kehidupan untuk menghasilkan harmoni sosial dalam masyarakat sebagaimana wiras wara yang menghasilkan suara yang merdu nan mengandung motivasi kepada siapa pun yang mendengarkan sehingga merasa penonton maupun pemain merasa sangat terhibur', 'NaN', 'NaN', NULL, NULL, undefined, 'undefined', 1, 'DRAFT', '2025-11-12T07:40:18.158Z', '2025-11-12T07:40:18.158Z');
